mapper.select:
select userId, userpwd, usernum, username, age, userid, userpwd, address, hp1, hp2, hp3, departnum, userrolelevel, gender from user_info where 1=1
and userid = #{userId}
and usernum = #{userNum}

mapper.update:
	update user_info
	set username = #{username}
  
  	, age =#{age}
  
  where usernum=#{usernum}
